import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import API_URL from "../constants";

function ProductDetail() {
    const [product, setProduct] = useState(null); // Initialize product state
    const [user, setUser] = useState(null); // Initialize user state
    const [showContact, setShowContact] = useState(false); // Control visibility of contact details
    const p = useParams(); // Get product ID from URL parameters
    const navigate = useNavigate(); // For navigation

    useEffect(() => {
        const url = `${API_URL}/get-product/${p.productId}`;
        axios.get(url)
            .then((res) => {
                if (res.data.product) {
                    setProduct(res.data.product);
                } else {
                    console.error("Product data is empty or missing.");
                }
            })
            .catch((err) => {
                alert('Server Error.');
                console.error("Error fetching product details:", err);
            });
    }, [p.productId]);

    const handleContact = (addedBy) => {
        const url = `${API_URL}/get-user/${addedBy}`;
        axios.get(url)
            .then((res) => {
                if (res.data.user) {
                    setUser(res.data.user);
                    setShowContact(true);
                } else {
                    console.log("User data is empty or missing in response."); // Debugging
                }
            })
            .catch((err) => {
                alert('Server Error.');
                console.error("Error fetching user data:", err);
            });
    };

    const handlePayNow = () => {
        navigate('/payment', { state: { product } }); // Navigate to payment with product info
    };

    return (
        <>
            <Header />
            <h2>PRODUCT DETAILS:</h2>
            <div>
                {product && (
                    <div className="d-flex justify-content-between flex-wrap">
                        <div>
                            <img width="400px" height="200px" src={`${API_URL}/${product.pimage}`} alt={product.pname} />
                            {product.pimage2 && <img width="400px" height="200px" src={`${API_URL}/${product.pimage2}`} alt={`${product.pname} alternate`} />}
                            <h6>Product Description:</h6>
                            <p>{product.pdesc}</p>
                        </div>
                        <div>
                            <h3 className="m-2 price-text">Rs. {product.price} /-</h3>
                            <p className="m-2">{product.pname} | {product.category}</p>

                            {/* Button to show contact details */}
                            {product.addedBy && (
                                <button onClick={() => handleContact(product.addedBy)}>
                                    SHOW CONTACT DETAILS
                                </button>
                            )}

                            {/* Display contact details if showContact is true */}
                            {showContact && user && (
                                <div className="mt-2">
                                    <h4>Username: {user.username}</h4>
                                    <h3>Mobile: {user.mobile ? user.mobile : 'Not available'}</h3>
                                    <h6>Email: {user.email ? user.email : 'Not available'}</h6>
                                    {/* Display pickup address if it exists */}
                                    {product.pickupAddress && (
                                        <div className="pickup-address">
                                            <h6>Pickup Address:</h6>
                                            <p>{product.pickupAddress}</p>
                                        </div>
                                    )}
                                </div>
                            )}

                            <div className="mt-3">
                                <button className="btn btn-primary me-2" onClick={handlePayNow}>
                                    Pay Now
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
}

export default ProductDetail;
