import React, { useState, useEffect } from "react";
import axios from "axios"; // For backend integration
import "./Chat.css"; // Import styles for the chat component

const Chat = () => {
  const [messages, setMessages] = useState([]); // Stores chat messages
  const [newMessage, setNewMessage] = useState(""); // For new messages
  const [loading, setLoading] = useState(true); // Tracks loading state

  // Fetch messages from the backend when the component mounts
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get("/api/messages"); // Replace with your backend URL
        setMessages(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchMessages();
  }, []);

  // Handle sending a new message
  const sendMessage = async () => {
    if (!newMessage.trim()) return; // Prevent sending empty messages

    try {
      const response = await axios.post("/api/messages", {
        text: newMessage,
      }); // Replace with your backend URL
      setMessages([...messages, response.data]); // Append the new message
      setNewMessage(""); // Clear the input
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div className="chat-container">
      <aside className="sidebar">
        <h3>SELLSPHERE</h3>
        <nav>
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/chat" className="active">Messages</a></li>
            <li><a href="/my-profile">Profile</a></li>
            <li><a href="/more-options">More Options</a></li>
          </ul>
        </nav>
      </aside>
      <main className="chat-main">
        <div className="message-list">
          <input type="text" placeholder="Search" className="search-bar" />
          <ul>
            {loading ? (
              <p>Loading messages...</p>
            ) : (
              messages.map((message, index) => (
                <li key={index} className={`message ${message.sender === "me" ? "sent" : "received"}`}>
                  {message.sender !== "me" && <strong>{message.sender}:</strong>} {message.text}
                </li>
              ))
            )}
          </ul>
        </div>
        <div className="chat-window">
          <div className="chat-header">Chat</div>
          <div className="messages">
            {messages.map((message, index) => (
              <div key={index} className={`message-row ${message.sender === "me" ? "sent" : "received"}`}>
                <div className={`message-box ${message.sender === "me" ? "sent" : "received"}`}>
                  {message.text}
                </div>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Chat;
