import React, { useState } from 'react';
import axios from 'axios';

const Checkout = () => {
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [message, setMessage] = useState('');

    const handlePayment = async () => {
        const data = {
            name: "John Doe",
            mobileNumber: 1234567890,
            amount: 100,
            cardNumber,
            expiryDate,
            cvv,
        };
        
        if (!validatePaymentDetails()) {
            setMessage('Invalid payment details. Please check your information.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8000/create-order', data);
            console.log(response.data);
            window.location.href = response.data.url; // Redirect to payment gateway
        } catch (error) {
            console.error("Error in payment", error);
            setMessage('Payment processing failed. Please try again.');
        }
    };

    const validatePaymentDetails = () => {
        // Basic validation for payment details
        return (
            cardNumber.length === 16 &&
            expiryDate.match(/^(0[1-9]|1[0-2])\/\d{2}$/) && // Format MM/YY
            cvv.length === 3
        );
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen">
            <h1 className="text-2xl font-bold mb-4">Checkout</h1>
            <div className="mb-4">
                <label className="block mb-1">
                    Card Number:
                    <input
                        type="text"
                        value={cardNumber}
                        onChange={(e) => setCardNumber(e.target.value)}
                        maxLength="16"
                        className="border border-gray-300 rounded px-2 py-1 w-full"
                    />
                </label>
                <label className="block mb-1">
                    Expiry Date (MM/YY):
                    <input
                        type="text"
                        value={expiryDate}
                        onChange={(e) => setExpiryDate(e.target.value)}
                        placeholder="MM/YY"
                        className="border border-gray-300 rounded px-2 py-1 w-full"
                    />
                </label>
                <label className="block mb-1">
                    CVV:
                    <input
                        type="text"
                        value={cvv}
                        onChange={(e) => setCvv(e.target.value)}
                        maxLength="3"
                        className="border border-gray-300 rounded px-2 py-1 w-full"
                    />
                </label>
            </div>
            <button
                onClick={handlePayment}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg"
            >
                Pay Now
            </button>
            {message && <p className="mt-4 text-red-600">{message}</p>}
        </div>
    );
};

export default Checkout;
