import React, { useState } from "react";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState({
    "456": [
      { sender: "123", text: "Hi Alice!", id: 1 },
      { sender: "456", text: "Hello! How can I help you?", id: 2 },
    ],
    "789": [
      { sender: "123", text: "Hi Bob!", id: 1 },
      { sender: "789", text: "Hey there! What's up?", id: 2 },
    ],
    "101": [
      { sender: "123", text: "Hi Charlie!", id: 1 },
      { sender: "101", text: "Hi! Long time no see.", id: 2 },
    ],
  });

  const [newMessage, setNewMessage] = useState("");
  const [currentChatUser, setCurrentChatUser] = useState("456");
  const userId = "123"; // Simulated current user ID

  // Simulated users for the sidebar
  const users = [
    { id: "456", name: "Alice" },
    { id: "789", name: "Bob" },
    { id: "101", name: "Charlie" },
  ];

  // Send a new message
  const sendMessage = () => {
    if (!newMessage.trim()) return;

    // Add the new message to the messages list
    const newMsg = {
      sender: userId,
      text: newMessage,
      id: messages[currentChatUser].length + 1,
    };

    // Simulate recipient's reply
    const simulatedReply = {
      sender: currentChatUser,
      text: "Got it! Let me check.",
      id: messages[currentChatUser].length + 2,
    };

    setMessages((prevMessages) => ({
      ...prevMessages,
      [currentChatUser]: [...prevMessages[currentChatUser], newMsg],
    }));
    setNewMessage("");

    // Simulate a delay for the reply
    setTimeout(() => {
      setMessages((prevMessages) => ({
        ...prevMessages,
        [currentChatUser]: [...prevMessages[currentChatUser], simulatedReply],
      }));
    }, 1000);
  };

  return (
    <div className="chat-container">
      <aside className="sidebar">
        <h3>SELLSPHERE</h3>
        <nav>
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/chat" className="active">Messages</a></li>
            <li><a href="/my-profile">Profile</a></li>
          </ul>
        </nav>
        <div className="user-list">
          <h4>Users</h4>
          <input type="text" placeholder="Search Users" className="search-bar" />
          <ul>
            {users.map((user) => (
              <li
                key={user.id}
                className={currentChatUser === user.id ? "active-user" : ""}
                onClick={() => setCurrentChatUser(user.id)}
              >
                {user.name}
              </li>
            ))}
          </ul>
        </div>
      </aside>
      <main className="chat-main">
        <div className="chat-window">
          <div className="chat-header">Chat with {users.find((u) => u.id === currentChatUser)?.name}</div>
          <div className="messages">
            {messages[currentChatUser]?.map((message) => (
              <div
                key={message.id}
                className={`message-row ${message.sender === userId ? "sent" : "received"}`}
              >
                <div
                  className={`message-box ${message.sender === userId ? "sent" : "received"}`}
                >
                  {message.text}
                </div>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Chat;
