import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import API_URL from "../constants";

function ProductDetail() {
    const [product, setproduct] = useState();
    const [user, setuser] = useState();
    const [showContact, setShowContact] = useState(false);
    const p = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const url = API_URL + '/get-product/' + p.productId;
        axios.get(url)
            .then((res) => {
                if (res.data.product) {
                    setproduct(res.data.product);
                }
            })
            .catch((err) => {
                alert('Server Error.');
            });
    }, [p.productId]);

    const handleContact = (addedBy) => {
        const url = API_URL + '/get-user/' + addedBy;
        axios.get(url)
            .then((res) => {
                if (res.data.user) {
                    setuser(res.data.user);
                    setShowContact(true);
                }
            })
            .catch((err) => {
                alert('Server Error.');
            });
    };

    const handlePayNow = () => {
        console.log("Navigating to payment for product:", product.pname);
        navigate('/payment', { state: { product } });
    };

    return (
        <>
            <Header />
            <h2>PRODUCT DETAILS:</h2>
            <div>
                {product && (
                    <div className="d-flex justify-content-between flex-wrap">
                        <div>
                            <img width="400px" height="200px" src={API_URL + '/' + product.pimage} alt="" />
                            {product.pimage2 && <img width="400px" height="200px" src={API_URL + '/' + product.pimage2} alt="" />}
                            <h6>Product Details:</h6>
                            {product.pdesc}
                        </div>
                        <div>
                            <h3 className="m-2 price-text">Rs. {product.price} /-</h3>
                            <p className="m-2">{product.pname} | {product.category}</p>
                            <p className="m-2 text-success">{product.pdesc}</p>

                            {product.addedBy && (
                                <button onClick={() => handleContact(product.addedBy)}>
                                    SHOW CONTACT DETAILS
                                </button>
                            )}
                            {showContact && user && (
                                <div className="mt-2">
                                    <h4>Username: {user.username}</h4>
                                    <h3>Mobile: {user.mobile}</h3>
                                    <h6>Email: {user.email}</h6>
                                </div>
                            )}

                            <div className="mt-3">
                                <button className="btn btn-primary me-2" onClick={handlePayNow}>
                                    Pay Now
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
}

export default ProductDetail;
