import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function PaymentPage() {
    const location = useLocation();
    const { product } = location.state || {}; // Destructure the product from state

    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [message, setMessage] = useState('');

    const handlePayment = () => {
        // Mock payment processing logic
        if (validatePaymentDetails()) {
            // Simulate a successful payment transaction
            setMessage('Payment successful! Thank you for your purchase.');
            // Here you would call your payment gateway API
        } else {
            setMessage('Invalid payment details. Please check your information.');
        }
    };

    const validatePaymentDetails = () => {
        // Basic validation for payment details
        return (
            cardNumber.length === 16 &&
            expiryDate.match(/^(0[1-9]|1[0-2])\/\d{2}$/) && // Format MM/YY
            cvv.length === 3
        );
    };

    return (
        <div>
            <h2>Payment Options</h2>
            {product ? (
                <div>
                    <h3>{product.pname}</h3>
                    <p>Price: Rs. {product.price} /-</p>
                    
                    <div>
                        <label>
                            Card Number:
                            <input
                                type="text"
                                value={cardNumber}
                                onChange={(e) => setCardNumber(e.target.value)}
                                maxLength="16"
                            />
                        </label>
                        <br />
                        <label>
                            Expiry Date (MM/YY):
                            <input
                                type="text"
                                value={expiryDate}
                                onChange={(e) => setExpiryDate(e.target.value)}
                                placeholder="MM/YY"
                            />
                        </label>
                        <br />
                        <label>
                            CVV:
                            <input
                                type="text"
                                value={cvv}
                                onChange={(e) => setCvv(e.target.value)}
                                maxLength="3"
                            />
                        </label>
                        <br />
                        <button onClick={handlePayment}>Pay Now</button>
                    </div>
                    {message && <p>{message}</p>}
                </div>
            ) : (
                <p>No product selected for payment.</p>
            )}
        </div>
    );
}

export default PaymentPage;
