import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import API_URL from "../constants";

function ProductDetail() {

    const [product, setproduct] = useState();
    const [user, setuser] = useState();
    console.log(user, "userrrrr");
    const p = useParams();

    useEffect(() => {
        const url = API_URL + '/get-product/' + p.productId;
        axios.get(url)
            .then((res) => {
                if (res.data.product) {
                    setproduct(res.data.product);
                }
            })
            .catch((err) => {
                alert('Server Err.');
            });
    }, [p.productId]);

    const handleContact = (addedBy) => {
        console.log('id', addedBy);
        const url = API_URL + '/get-user/' + addedBy;
        axios.get(url)
            .then((res) => {
                if (res.data.user) {
                    setuser(res.data.user);
                }
            })
            .catch((err) => {
                alert('Server Err.');
            });
    };

    // Placeholder function for adding to cart
    
        const handlepaynow = () => {
            console.log("Product :", product.pname);
            // Navigate to payment page with product details
            history.push({
                pathname: '/payment',
                state: {
                    productId: product._id,
                    productName: product.pname,
                    productPrice: product.price,
                }
            });
        };

    // Placeholder function for adding to wishlist
    const handleAddToWishlist = () => {
        console.log("Product added to wishlist:", product.pname);
        // Implement add to wishlist logic here
    };

    return (
        <>
            <Header />
            <h2>PRODUCT DETAILS:</h2>
            <div>
                {product && (
                    <div className="d-flex justify-content-between flex-wrap">
                        <div>
                            <img width="400px" height="200px" src={API_URL + '/' + product.pimage} alt="" />
                            {product.pimage2 && <img width="400px" height="200px" src={API_URL + '/' + product.pimage2} alt="" />}
                            <h6>Product Details:</h6>
                            {product.pdesc}
                        </div>
                        <div>
                            <h3 className="m-2 price-text">Rs. {product.price} /-</h3>
                            <p className="m-2">{product.pname} | {product.category}</p>
                            <p className="m-2 text-success">{product.pdesc}</p>

                            {product.addedBy && (
                                <button onClick={() => handleContact(product.addedBy)}>
                                    SHOW CONTACT DETAILS
                                </button>
                            )}
                            {user && user.username && <h4>{user.username}</h4>}
                            {user && user.mobile && <h3>{user.mobile}</h3>}
                            {user && user.email && <h6>{user.email}</h6>}

                            <div className="mt-3">
                                <button className="btn btn-primary me-2" onClick={handleAddToCart}>
                                    Pay Now
                                </button>
                                <button className="btn btn-secondary" onClick={handleAddToWishlist}>
                                    ADD TO WISHLIST
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
}

export default ProductDetail;
