import { useEffect, useState } from "react";
import Header from "./Header";
import axios from "axios";
import API_URL from "../constants";

function MyProfile() {
    const [user, setUser] = useState(null); // Initialize user state

    useEffect(() => {
        const userId = localStorage.getItem('userId'); // Get the user ID from localStorage
        if (!userId) {
            alert('User not logged in.');
            return;
        }
        
        const url = `${API_URL}/my-profile/${userId}`; // Correctly format the URL
        axios.get(url)
            .then((res) => {
                if (res.data.user) {
                    setUser(res.data.user);
                } else {
                    alert('User not found.');
                }
            })
            .catch((err) => {
                alert('Server Error.');
                console.error("Error fetching user profile:", err); // Log the error for debugging
            });
    }, []); // Run once on mount

    return (
        <div>
            <Header />
            <div className="m-3 p-3">
                <h3 className="text-center mt-2">USER PROFILE</h3>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <td>USERNAME</td>
                            <td>EMAIL ID</td>
                            <td>MOBILE</td>
                            <td>PICK-UP ADDRESS</td> {/* Add Address Header */}
                        </tr>
                    </thead>
                    <tbody>
                        {user ? (
                            <tr>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td>{user.mobile ? user.mobile : 'Not available'}</td>
                                <td>{user.address ? user.address : 'Not available'}</td> {/* Display Address */}
                            </tr>
                        ) : (
                            <tr>
                                <td colSpan="4" className="text-center">Loading user profile...</td> {/* Loading message */}
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default MyProfile;
