                <div class="message-item active" data-chat="1">Sourabh Pandey</div>
                <div class="message-item" data-chat="2">Mukul Nandaniya</div>
                <div class="message-item" data-chat="3">Shrivanshu Dube</div>
                <!-- Add more message items as needed -->
            </div>

            <div class="chat-box">
                <div class="chat-box-header">
                    <h3>Sourabh Pandey</h3>
                    <span>@sourabh</span>
                </div>
                <div class="chat-messages">
                    <!-- Messages will be displayed here -->
                </div>

                <div class="chat-input">
                    <textarea id="message-input" placeholder="Type a message"></textarea>
                    <button id="send-message" class="send-btn">Send</button>
                </div>
            </div>
        </div>
    </div>
`;
document.body.innerHTML = htmlContent;

// Message data structure
const messagesData = {
  1: [{
    sender: "Sourabh",
    text: "Hi my friend"
  }, {
    sender: "You",
    text: "Hello!"
  }],
  2: [{
    sender: "Mukul",
    text: "How are you?"
  }, {
    sender: "You",
    text: "I am good, thanks!"
  }],
  3: [{
    sender: "Shrivanshu",
    text: "How are you?"
  }, {
    sender: "You",
    text: "I'm doing well!"
  }]
};

// Set active chat based on message item click
const messageItems = document.querySelectorAll('.message-item');
const chatMessagesContainer = document.querySelector('.chat-messages');
const sendMessageButton = document.querySelector('#send-message');
const messageInput = document.querySelector('#message-input');
messageItems.forEach(item => {
  item.addEventListener('click', () => {
    // Remove active class from all items
    messageItems.forEach(i => i.classList.remove('active'));
    item.classList.add('active');

    // Load the messages for the selected chat
    const chatId = item.getAttribute('data-chat');
    loadMessages(chatId);
  });
});

// Load messages into the chat box
function loadMessages(chatId) {
  chatMessagesContainer.innerHTML = ''; // Clear previous messages
  const messages = messagesData[chatId];
  messages.forEach(msg => {
    const msgElement = document.createElement('div');
    msgElement.textContent = `${msg.sender}: ${msg.text}`;
    chatMessagesContainer.appendChild(msgElement);
  });
}

// Send message functionality
sendMessageButton.addEventListener('click', () => {
  const messageText = messageInput.value.trim();
  if (messageText) {
    const activeChat = document.querySelector('.message-item.active');
    const chatId = activeChat.getAttribute('data-chat');
    messagesData[chatId].push({
      sender: "You",
      text: messageText
    });
    loadMessages(chatId);
    messageInput.value = ''; // Clear the input field
  }
});

