import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import axios from 'axios';
import Header from './Header'; // Adjust the path if needed

function ProductDetail() {
    const [product, setProduct] = useState(null); // Initializing with null for conditional rendering
    const { productId } = useParams(); // Destructured for clarity
    const navigate = useNavigate(); // Hook for navigation

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const url = `http://localhost:4000/get-product/${productId}`;
                const res = await axios.get(url);
                if (res.data && res.data.product) {
                    setProduct(res.data.product);
                } else {
                    alert('Product not found');
                }
            } catch (err) {
                console.error(err);
                alert('Something went wrong. Please try again later.');
            }
        };

        fetchProduct();
    }, [productId]);

    const handlePayNow = () => {
        // Logic for payment processing, e.g., redirecting to a payment page
        navigate(`/payment/${productId}`); // Redirect to a payment page with the product ID
    };

    return (
        <>
            <Header />
            <div className="container mt-4">
                <h3>Product Details</h3>
                {product ? (
                    <div className="d-flex justify-content-between flex-wrap">
                        <div>
                            <img
                                width="700px"
                                height="300px"
                                src={`http://localhost:4000/${product.pimage}`}
                                alt={product.pname || 'Product Image'}
                            />
                            <h6>Product Description:</h6>
                            <p>{product.pdesc}</p>
                        </div>
                        <div className="m-2">
                            <h4>{product.pname}</h4>
                            <p>Category: {product.category}</p>
                            <p className="text-success">{product.pdesc}</p>
                            <h3 className="text-danger">${product.price}</h3>
                            <button 
                                className="btn btn-primary" 
                                onClick={handlePayNow}
                            >
                                Pay Now
                            </button>
                        </div>
                    </div>
                ) : (
                    <p>Loading product details...</p>
                )}
            </div>
        </>
    );
}

export default ProductDetail;
