import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import paymentQrImage from './Images/payment_qr.jpg'; // Adjust the path accordingly
import './PaymentPage.css';

function PaymentPage() {
    const location = useLocation();
    const { product } = location.state || {}; // Destructure the product from state

    const [addresses, setAddresses] = useState([]); // State to store fetched addresses
    const [selectedAddress, setSelectedAddress] = useState('');
    const [transactionId, setTransactionId] = useState('');
    const [email, setEmail] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('');
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(true); // For handling loading state
    const [error, setError] = useState(null); // For handling errors

    // Fetch addresses from the backend on component mount
    useEffect(() => {
        fetch('/api/addresses') // Replace with your actual API endpoint
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setAddresses(data); // Set the fetched addresses in state
                setLoading(false); // Set loading to false once data is fetched
            })
            .catch((error) => {
                setError(error.message); // Set error message if fetch fails
                setLoading(false);
            });
    }, []);

    const handlePayment = () => {
        if (!selectedAddress || !paymentMethod) {
            setMessage('Please select a delivery address and payment method.');
            return;
        }

        // Simulate a successful payment transaction
        setMessage('Payment successful! Thank you for your purchase.');
    };

    return (
        <div className="payment-page-container">
            <h1 className="header">SELLSPHERE</h1>

            <div className="main-container">
                {/* Delivery Address Section */}
                <section className="section">
                    <h2>Delivery Address</h2>
                    {loading && <p>Loading addresses...</p>}
                    {error && <p className="error-message">Error: {error}</p>}
                    <div className="address-box">
                        {addresses.map((address) => (
                            <div
                                key={address.id}
                                className={`address ${selectedAddress === address.id ? 'selected' : ''}`}
                                onClick={() => setSelectedAddress(address.id)}
                            >
                                <h3>{address.name}</h3>
                                <p>{address.details}</p>
                                <p>Mobile No: {address.mobile}</p>
                            </div>
                        ))}
                    </div>
                </section>

                {/* Order Summary Section */}
                <section className="section">
                    <h2>Order Summary</h2>
                    {product ? (
                        <div className="order-summary">
                            <img src={product.image} alt={product.pname} />
                            <div>
                                <h3>{product.pname}</h3>
                                <p>Price: Rs. {product.price} /-</p>
                                <p>{product.description}</p>
                            </div>
                        </div>
                    ) : (
                        <p>No product selected for payment.</p>
                    )}
                </section>

                {/* Payment Details Section */}
                <section className="section">
                    <h2>Payment Details</h2>
                    <div className="payment-methods">
                        <label>
                            <input
                                type="radio"
                                name="payment"
                                value="card"
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            Credit/Debit Card
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="payment"
                                value="netbanking"
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            Net Banking
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="payment"
                                value="cod"
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            Cash on Delivery
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="payment"
                                value="upi"
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            UPI
                        </label>
                    </div>

                    {/* QR Payment Section */}
                    <div className="qr-section">
                        <img src={paymentQrImage} alt="QR Code for payment" />
                        <p>Please confirm your payment details:</p>
                        <label>
                            Transaction ID:
                            <input
                                type="text"
                                value={transactionId}
                                onChange={(e) => setTransactionId(e.target.value)}
                            />
                        </label>
                        <label>
                            Email Address:
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </label>
                    </div>
                </section>
            </div>

            {/* Pay Now Section */}
            <div className="pay-now">
                <button onClick={handlePayment}>Pay Now</button>
                {message && <p className="payment-message">{message}</p>}
            </div>
        </div>
    );
}

export default PaymentPage;
