import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, useParams } from "react-router-dom";
import axios from "axios";
import PaymentButton from "./components/PaymentButton"; // Ensure PaymentButton is correctly located

// Header Component (if needed)
function Header() {
  return <header><h1>My Store</h1></header>;
}

// ProductDetail Component
function ProductDetail() {
  const [product, setProduct] = useState(null);
  const { productId } = useParams();

  useEffect(() => {
    const url = 'http://localhost:4000/get-productt/' + productId;
    axios.get(url)
      .then((res) => {
        console.log(res);
        if (res.data.product) {
          setProduct(res.data.product);
        }
      })
      .catch((err) => {
        console.log(err);
        alert('Something went wrong');
      });
  }, [productId]);

  return (
    <>
      <Header />
      <div>
        <h2>PRODUCT DETAILS :</h2>
        {product ? (
          <div className="d-flex justify-content-between flex-wrap">
            <div>
              <img
                width="700px"
                height="300px"
                src={'http://localhost:4000/' + product.pimage}
                alt=""
              />
              <h6>Product Details:</h6>
              <p>{product.pdesc}</p>
            </div>
            <div>
              <p className="m-2">{product.pname} | {product.category}</p>
              <p className="m-2 text-success">{product.pdesc}</p>
              <h3 className="m-2 text-danger">{product.price}</h3>
              {/* Payment Button Component */}
              <PaymentButton />
            </div>
          </div>
        ) : (
          <p>Loading product details...</p>
        )}
      </div>
    </>
  );
}

// Main App Component
function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<h1>Welcome to the Store</h1>} /> {/* Home component */}
          <Route path="/product/:productId" element={<ProductDetail />} /> {/* Product detail page */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
