import { useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import React, { useState } from 'react';
import Header from './Header'; // Adjust the path based on your folder structure



function ProductDetail(){

    const [product , setproduct] = useState()
    const p = useParams()
  

    useEffect(()=>{
        const url = 'http://localhost:4000/get-productt/' + p.productId;
        axios.get(url)
        .then((res)=>{
            console.log(res);
            if(res.data.product){
                setproduct(res.data.product)
            }
        })
        .catch((err)=>{
            console.log(err);
            alert('something went wrong ')
        })
    },[])

    return (<>
        <Header/>
        PRODUCT DETAILS :
         <div >
            {product && <div className="d-flex justify-content-between flex-wrap">
                <div> 
                    <img width= "700px" height="300px" src={'http://localhost:4000/' + product.pimage } alt="" />
                    <h6>Product Details :</h6>
                    {product.pdesc}

