import React, { useState } from "react";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState([
    { sender: "123", text: "Hi there!", id: 1 },
    { sender: "456", text: "Hello! How can I help you?", id: 2 },
  ]);
  const [newMessage, setNewMessage] = useState("");
  const userId = "123"; // Simulated current user ID
  const otherUserId = "456"; // Simulated recipient user ID

  // Send a new message
  const sendMessage = () => {
    if (!newMessage.trim()) return;

    // Add the new message to the messages list
    const newMsg = {
      sender: userId,
      text: newMessage,
      id: messages.length + 1,
    };

    // Simulate recipient's reply
    const simulatedReply = {
      sender: otherUserId,
      text: "Got it! Let me check.",
      id: messages.length + 2,
    };

    setMessages((prevMessages) => [...prevMessages, newMsg]);
    setNewMessage("");

    // Simulate a delay for the reply
    setTimeout(() => {
      setMessages((prevMessages) => [...prevMessages, simulatedReply]);
    }, 1000);
  };

  return (
    <div className="chat-container">
      <aside className="sidebar">
        <h3>SELLSPHERE</h3>
        <nav>
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/chat" className="active">Messages</a></li>
            <li><a href="/my-profile">Profile</a></li>
          </ul>
        </nav>
      </aside>
      <main className="chat-main">
        <div className="message-list">
          <input type="text" placeholder="Search" className="search-bar" />
          <ul>
            {messages.map((message) => (
              <li
                key={message.id}
                className={`message ${message.sender === userId ? "sent" : "received"}`}
              >
                {message.sender !== userId && <strong>Other User:</strong>} {message.text}
              </li>
            ))}
          </ul>
        </div>
        <div className="chat-window">
          <div className="chat-header">Chat</div>
          <div className="messages">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`message-row ${message.sender === userId ? "sent" : "received"}`}
              >
                <div
                  className={`message-box ${message.sender === userId ? "sent" : "received"}`}
                >
                  {message.text}
                </div>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Chat;
