import { useEffect, useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import axios from "axios";
import API_URL from "../constants";

function PaymentPage() {
    const location = useLocation();
    const history = useHistory();
    const { productId, productName, productPrice } = location.state;

    const [paymentStatus, setPaymentStatus] = useState(null);

    const handlePayment = () => {
        // Here, you would integrate with a payment gateway
        // Simulate payment processing
        setTimeout(() => {
            setPaymentStatus('success'); // or 'failed'
            // After processing, you can redirect back to the product detail or home page
            // history.push("/"); // Redirect to home or wherever necessary
        }, 2000);
    };

    return (
        <div>
            <h2>Payment Page</h2>
            <h3>Product: {productName}</h3>
            <h4>Price: Rs. {productPrice} /-</h4>
            <button onClick={handlePayment}>Confirm Payment</button>

            {paymentStatus && (
                <div>
                    {paymentStatus === 'success' ? (
                        <h4>Payment Successful!</h4>
                    ) : (
                        <h4>Payment Failed. Please try again.</h4>
                    )}
                </div>
            )}
        </div>
    );
}

export default PaymentPage;
