import React from 'react';
import { useLocation } from 'react-router-dom';

function PaymentPage() {
    const location = useLocation();
    const { product } = location.state || {}; // Destructure the product from state

    return (
        <div>
            <h2>Payment Options</h2>
            {product ? (
                <div>
                    <h3>{product.pname}</h3>
                    <p>Price: Rs. {product.price} /-</p>
                    {/* Add payment method options here */}
                    <button>Credit/Debit Card</button>
                    <button>PayPal</button>
                    <button>UPI</button>
                    {/* You can implement the payment logic here */}
                </div>
            ) : (
                <p>No product selected for payment.</p>
            )}
        </div>
    );
}

export default PaymentPage;
