import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [userId, setUserId] = useState("123"); // Replace with actual user ID
  const [otherUserId, setOtherUserId] = useState("456"); // Replace with actual recipient user ID

  // Fetch messages when the component mounts
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/messages?userId=${userId}&otherUserId=${otherUserId}`);
        setMessages(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchMessages();
  }, [userId, otherUserId]);

  // Send a new message
  const sendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      const response = await axios.post("/api/messages", {
        sender: userId,
        recipient: otherUserId,
        text: newMessage,
      });
      setMessages([...messages, response.data]); // Add new message to the list
      setNewMessage(""); // Clear the input field
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div className="chat-container">
      <aside className="sidebar">
        <h3>SELLSPHERE</h3>
        <nav>
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/chat" className="active">Messages</a></li>
            <li><a href="/my-profile">Profile</a></li>
          </ul>
        </nav>
      </aside>
      <main className="chat-main">
        <div className="message-list">
          <input type="text" placeholder="Search" className="search-bar" />
          <ul>
            {loading ? (
              <p>Loading messages...</p>
            ) : (
              messages.map((message, index) => (
                <li key={index} className={`message ${message.sender._id === userId ? "sent" : "received"}`}>
                  {message.sender._id !== userId && <strong>{message.sender.username}:</strong>} {message.text}
                </li>
              ))
            )}
          </ul>
        </div>
        <div className="chat-window">
          <div className="chat-header">Chat</div>
          <div className="messages">
            {messages.map((message, index) => (
              <div key={index} className={`message-row ${message.sender._id === userId ? "sent" : "received"}`}>
                <div className={`message-box ${message.sender._id === userId ? "sent" : "received"}`}>
                  {message.text}
                </div>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Chat;
