import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import Header from './Header'; // Adjust the path if needed

function ProductDetail() {
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true); // State to track loading status
    const [error, setError] = useState(null); // State to track errors
    const { productId } = useParams();

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const url = `http://localhost:4000/get-product/${productId}`;
                const res = await axios.get(url);
                if (res.data && res.data.product) {
                    setProduct(res.data.product);
                } else {
                    setError('Product not found'); // Set error state
                }
            } catch (err) {
                console.error(err);
                setError('Something went wrong. Please try again later.'); // Set error state
            } finally {
                setLoading(false); // Stop loading regardless of success or failure
            }
        };

        fetchProduct();
    }, [productId]);

    // Function to format price as currency
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
        }).format(amount);
    };

    return (
        <>
            <Header />
            <div className="container mt-4">
                <h3>Product Details</h3>
                {loading ? (
                    <p>Loading product details...</p> // Loading indicator
                ) : error ? (
                    <div className="alert alert-danger">{error}</div> // Display error message
                ) : product ? (
                    <div className="d-flex justify-content-between flex-wrap">
                        <div>
                            {product.pimage ? ( // Check if image exists
                                <img
                                    width="700px"
                                    height="300px"
                                    src={`http://localhost:4000/${product.pimage}`}
                                    alt={product.pname || 'Product Image'}
                                />
                            ) : (
                                <div>No image available</div> // Fallback for missing images
                            )}
                            <h6>Product Description:</h6>
                            <p>{product.pdesc}</p>
                        </div>
                        <div className="m-2">
                            <h4>{product.pname}</h4>
                            <p>Category: {product.category}</p>
                            <p className="text-success">{product.pdesc}</p>
                            <h3 className="text-danger">{formatCurrency(product.price)}</h3> {/* Format price */}
                        </div>
                    </div>
                ) : (
                    <p>Product not found</p>
                )}
                <Link to="/products" className="btn btn-primary mt-3">Back to Products</Link> {/* Navigation link */}
            </div>
        </>
    );
}

export default ProductDetail;
