import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import "./Login.css"; // Add the stylesheet for the Login component

function Login() {
    const navigate = useNavigate();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);

    const handleApi = (e) => {
        e.preventDefault(); // Prevent default form submission
        setLoading(true);
        
        const url = 'http://localhost:4000/login';
        const data = { username, password };
        
        axios.post(url, data)
            .then((res) => {
                setLoading(false);
                console.log(res.data);
                alert(res.data.message);
                if (res.data.message && res.data.token) {
                    localStorage.setItem('token', res.data.token);
                    localStorage.setItem('userId', res.data.userId);
                    navigate('/');
                }
            })
            .catch((err) => {
                setLoading(false);
                console.error(err);
                alert('Server error, please try again later.');
            });
    };

    return (
        <div className="container">
            <div className="left-section">
                <div className="logo">
                    <h1>SellSphere</h1>
                </div>
                <div className="login-box">
                    <div className="login-signup">
                        <h2 className="active">Login</h2>
                        <Link to="/signup" className="inactive">Sign up</Link>
                    </div>
                    <form onSubmit={handleApi}>
                        <div className="input-group">
                            <input
                                type="text"
                                placeholder="Email or phone number"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <input
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit" className="login-btn" disabled={loading || !username || !password}>
                            {loading ? 'Logging in...' : 'Login'}
                        </button>
                    </form>
                </div>
            </div>
            <div className="right-section">
                <img src="illustration.png" alt="Illustration" className="illustration" />
            </div>
        </div>
    );
}

export default Login;
