import { useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import React, { useState } from 'react';
import Header from './Header'; // Adjust the path based on your folder structure



function ProductDetail(){

    const [product , setproduct] = useState()
    

    const p = useParams()
    console.log(p.productId)

    useEffect(()=>{
        const url = 'http://localhost:4000/get-productt/' + p.productId;
        axios.get(url)
        .then((res)=>{
            console.log(res);
            if(res.data.product){
                setproduct(res.data.product)
            }
        })
        .catch((err)=>{
            console.log(err);
            alert('something went wrong ')
        })
    },[])

    return (
        <div>
            <Header/>
            PRODUCT DETAILS ARE FETCHING PLEASE WAIT...
            {product && <div>
                <div> 
                    <img src={'http://localhost/4000/' + product.pimage } alt="" />

                </div>
                <div>

                </div>
            </div>}
        </div>
    );
}

export default ProductDetail;
